INSERT INTO `gallery_items` VALUES (1,'Bac Ha market','2014-12-29 08:12:33','2014-12-29 08:22:11','Kids at the market','Bac_Ha_market.jpg'),(2,'Bai Dinh Pagoda','2014-12-29 08:13:49','2014-12-29 08:13:49','Budda statues at Bai Dinh pagoda','Bai_Dinh_Pagoda.jpg'),(3,'My Son','2014-12-29 08:15:31','2014-12-29 08:18:55','My Son sanctuary','Thanh_Dia_My_Son.jpg'),(4,'Da Nang\'s Banh My','2014-12-29 08:25:28','2014-12-29 08:25:28','Bountiful sandwich','Banh_My.jpg'),(5,'Cho noi Cai Rang','2014-12-29 08:34:26','2014-12-29 08:34:26','Cho noi Cai Rang','Cho_noi_Cai_Rang.jpg'),(6,'Hoa Lu','2014-12-29 08:38:47','2014-12-29 08:38:47','Overlook of Hoa Lu','Hoa_Lu.jpg'),(7,'Vinh Ha Long upon dawn','2014-12-29 11:03:17','2014-12-29 11:03:17','Scene of Vinh Ha Long at dawn','Vinh_Ha_Long_s_dawn.jpg'),(8,'Hard coral','2014-12-29 11:08:28','2014-12-29 11:08:28','Scene of coral below the sea surface','Hard_coral.jpg'),(9,'Phong Nha Ke Bang\'s cave','2014-12-29 11:17:55','2014-12-29 11:17:55','Illuminating cave of Phong Nha Ke Bang','Phong_Nha_Ke_Bang.jpg');
